import React, { useState, useEffect } from 'react';
import { NavLink } from "react-router-dom";
import { useDispatch, useSelector } from 'react-redux';
import { addCart } from '../../../redux/action';
import useFirstRender from '../../../hooks/use-first-ref.tsx';
import useHasChanged from '../../../hooks/use-has-changed.tsx';

const PLPProduct = (props) => {

  const [active, setActive] = useState(null)
  console.log(active)
  const cart = useSelector((cart) => cart.handleCart);
  let cartNum = cart.reduce(function (acc, obj) { return acc + obj.qty; }, 0);

  //Check if these values have changed
  const activeHasChanged = useHasChanged(active)
  const cartNumHasChanged = useHasChanged(cartNum)
  const isFirstRender = useFirstRender();

  useEffect(() => {

    if(active === null) {
      alert("STOP")
    }

    if (cartNumHasChanged || activeHasChanged) {
     return;
    } else {
      alert("WOAH! You added the maximum amount of this product!")
    }
  });

  //For products with sizes
  const updatedSku = active ? props.sku + "-" + active : props.sku;

  const dispatch = useDispatch();
  const addProduct = (props) => {
      dispatch(addCart({
        ...props,
        sku: updatedSku,
        selected_size: active
      }));
  }

  let qty_wording = ""

  if(props.quantity_available <= 10 && props.quantity_available != 1) {
    qty_wording = <p>Only {props.quantity_available} left!</p>
  } else if(props.quantity_available == 1) {
    qty_wording = <p>LAST ONE!</p>
  } else if(props.quantity_available == 0) {
    qty_wording = <p>OUT OF STOCK!</p>
  } else {
    qty_wording = <p>{props.quantity_available} available</p>
  }

  return (
    <li>
      <h2>{props.title}</h2>
      <h3><strike>${parseFloat(props.msrp).toFixed(2)}</strike></h3>
      <h3>${parseFloat(props.price).toFixed(2)}</h3>
      <h4>{qty_wording}</h4>
      <img src={props.image} width="500" height="700" />
      <h3>{props.brand}</h3>
      <p>{props.description}</p>
      <br />
      {props.sizes?.map((size) => (
        <button onClick={() => setActive(size)}
        className={`btn ${active == size ? 'active' : 'not-selected'}`}
        >{size}</button>
      ))}
      <br />
      <button
            className="btn btn-outline-dark px-4 py-2"
            onClick={() => addProduct(props)}
          >
            Add to Cart
          </button>

                   <NavLink to="/cart" className="btn btn-dark ms-2 px-3 py-2">
                       Go To Cart
                   </NavLink>
    </li>
  );
};

export default PLPProduct;
