import React, { useState, useEffect, useRef, useCallback } from 'react';
import ProductDisplayPage from './ProductDisplayPage';
import Filters from '../../Filters/Filters';

const Products = (props) => {

    const [loading, setLoading] = useState(false);
    const [products ,setProducts] = useState([]);
    const [search, setSearch] = useState("");
    const [query, setQuery] = useState("");
    const [currentPage, setCurrentPage] = useState(1)
    const [filters, setFilters] = useState({});
    const [pageData, setPageData] = useState({});
    const [activeFilters, setActiveFilters] = useState({})
    const filterRef = useRef(null)

    // const getFilters = () => {
    //   return activeFilters.target.value;
    // }

    console.log(activeFilters)

    useEffect(() => {
      fetchProducts(currentPage);
    }, [query, currentPage]);

    console.log("FILTERS", filters)

    const fetchProducts = useCallback(async () => {

      setLoading(true)

        try {

          let selectedFilters = "&filter.color_family=Blue"

            const response = await fetch(`https://scmq7n.a.searchspring.io/api/search/search.json?resultsFormat=native&siteId=scmq7n&q=${query}&page=${currentPage}` + selectedFilters);

            if(!response.ok) {
              throw new Error("Something went wrong :(")
            }

            const data = await response.json();

            const transformedProducts = data.results.map(result =>
              {
                return ({
                  id: result.id,
                  sku: result.sku,
                  title: result.title,
                  image: result.imageUrl,
                  price: result.price,
                  msrp: result.msrp,
                  url: result.url,
                });    
              }
            );

            setPageData(data.pagination);
            setFilters(data.facets)

            setProducts(transformedProducts)

          } catch (error) {
            console.log(error)
        }

     setLoading(false);
      }, [query, currentPage]);

      const updateSearch = (e) => {
        setSearch(e.target.value)
      }

      const getSearch = (e) => {
        e.preventDefault();
        setQuery(search)
        setSearch('')
      }


      const prevPageHandler = () => {
        setCurrentPage(pageData.currentPage - 1)
      }

      const nextPageHandler = () => {
        setCurrentPage(pageData.currentPage + 1)
      }

      return (
        <>
        <form onSubmit={getSearch}>
            <input type="text" value={search} onChange={updateSearch}/>
            <button type="submit">Submit</button>
         </form>
        {loading ? <p>Loading...</p> : ''}
        {products.length == 0 && !loading && <p>No products</p>}
        {products.length > 0 && !loading && 
        <div>
        <Filters filters={filters} ref={filterRef} onClick={setActiveFilters(filterRef.current.onOptionChanger())}/>
        <p>{pageData.totalResults} results</p>
        <ProductDisplayPage products={products}/>
        <p>Page {pageData.currentPage} of {pageData.totalPages}</p>
        {pageData.previousPage !== 0 && <button value={pageData.previousPage} onClick={prevPageHandler}>←</button>}
        {pageData.nextPage !== 0 && <button value={pageData.nextPage} onClick={nextPageHandler}>→</button>}
        </div>}
        </>
      )
}

export default Products;
