import React, { useState, useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";
import { NavLink } from "react-router-dom";
import { addCart, deleteCart } from "../../redux/action"

import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';

const Cart = () => {

  const state = useSelector((state) => state.handleCart);
  const total = state.reduce(function (acc, obj) { return acc + obj.qty * obj.price; }, 0);
  console.log(total)

  const dispatch = useDispatch();

  const handleAdd = (item) => {
    dispatch(addCart(item));
  };
  const handleDelete = (item) => {
    dispatch(deleteCart(item));
  };

  const emptyCart = () => {
    return (
      <div className="px-4 my-5 bg-light rounded-3 py-5">
        <div className="container py-4">
          <div className="row">
            <h3>Your Cart is Empty</h3>
          </div>
        </div>
      </div>
    );
  };

  const cartItems = (product) => {

    return (
      <>
       <Container>
      <Row>
      <Col sm={6} md={6} lg={3}>
                <img
                  src={product.image}
                  alt={product.title}
                  height="300px"
                  width="250px"
                />
        </Col>
        <Col sm={6} md={6} lg={3}>
                <h3>{product.title}</h3>
                {product.selected_size && <div>SIZE: {product.selected_size}</div>}
            </Col>
            <Col sm={6} md={6} lg={3}>
            <h3>Quantity</h3>
                <p>
                  {product.qty} X ${parseInt(product.price).toFixed(2)} = $
                  {(product.qty * product.price).toFixed(2)}
                </p>
                <button
                  className="btn btn-outline-dark me-4"
                  onClick={() => handleDelete(product)}
                >
                  <i className="fa fa-minus">-</i>
                </button>
                <button
                  className="btn btn-outline-dark add-btn"
                  disabled={parseFloat(product.quantity_available) >= product.qty + 1 ? false : true}
                  onClick={() => handleAdd(product)}
                >
                  <i className="fa fa-plus">+</i>
                </button>
                {parseFloat(product.quantity_available) >= product.qty + 1 ? ('') : (
                  <span>You added the maximum amount of this product!</span>
                )}
              </Col>
              <Col sm={6} md={6} lg={3}>
                <h3>Total</h3>
              </Col>
         </Row>
         </Container>
      </>
    );
  };

  const buttons = () => {
    return (
      <>
        <div className="container">
          <div className="row">
          <p>TOTAL = ${total.toFixed(2)}</p>
            <NavLink
              to="/checkout"
              className="btn btn-outline-dark mb-5 w-25 mx-auto"
            >
              Proceed to Checkout
            </NavLink>
          </div>
        </div>
      </>
    );
  };

  return (
    <div>
      {state.length === 0 && emptyCart()}
      {state.length !== 0 && state.map(cartItems)}
      {state.length !== 0 && buttons()}
    </div>
  );

}

export default Cart
