import React, {useState} from 'react';
import Filter from "./Filter"
import Sliders from './Sliders';

const Filters = (props) => {

  const [filter, setFilter] = useState({})

  const onFacetChange = (field) => {
    setFilter({
        active: 1,
        activeVal: field,
    })
}

console.log(filter)

return (
    <div>
    <h2>FILTERS</h2>
    <span onChange={props.onChange}>
    {props.filters.map((filter) => (
      <div onChange={onFacetChange}>
      {filter.type != 'slider' ?
          (
          <Filter
            active={filter.facet_active}
            label={filter.label}
            field={filter.field}
            values={filter.values}
          />) : (
            <Sliders 
            active={filter.facet_active}
            label={filter.label}
            field={filter.field}
            range={filter.range}
            />
            )}
      </div>
        ))}
        </span>
    </div>
)

}

export default Filters;
