import React from 'react';
import { NavLink } from "react-router-dom";
import Col from 'react-bootstrap/Col';

import classes from "./PDPProduct.module.css"

const PDPProduct = (props) => {
  return (
    <>
    <Col md={3} lg={4} className={classes.container}>
      <h2>{props.title}</h2>
      <h3><strike>${parseFloat(props.msrp).toFixed(2)}</strike></h3>
      <h3>${parseFloat(props.price).toFixed(2)}</h3>
      <img src={props.image} width="350" height="550" />
      <br />
      <NavLink to={{ pathname:"/product/" +  `${props.sku}` }}>
      <button>Buy Now</button>
      </NavLink>
      </Col>
    </>
  );
};

export default PDPProduct;
