import React, {useState} from 'react';
import Filter from "./Filter"
import Sliders from './Sliders';

const Filters = (props) => {

  const [facet, setFacet] = useState({})

  const onChangeHandler = () => {
    setFacet({
      ...props,
        active: 1,
        activeFacet: "x",
    })
}

console.log(facet)

return (
    <div>
    <h2>FILTERS</h2>
    <span onChange={props.onChange}>
    {props.filters.map((filter) => (
      <span>
      {filter.type != 'slider' ?
          (
          <Filter
            value={facet}
            onChange={onChangeHandler}
            active={filter.facet_active}
            label={filter.label}
            field={filter.field}
            values={filter.values}
          />) : (
            <Sliders 
            active={filter.facet_active}
            label={filter.label}
            field={filter.field}
            range={filter.range}
            />
            )}
      </span>
        ))}        
        </span>
    </div>
)

}

export default Filters;
