import React, {useState} from 'react';
import Filter from "./Filter"
import Sliders from './Sliders';

const Filters = (props) => {

  const [filter, setFilter] = useState({})

  console.log(props)

  const onChangeHandler = () => {
      setFilter({
          active: 1,
          activeFacet: 'x',
      })
  }

  console.log("F", filter)

return (
    <div>
    <h2>FILTERS</h2>
    <span value={filter} onChange={props.onChange}>
    {props.filters.map((filter) => (
      <span onChange={onChangeHandler}>
      {filter.type != 'slider' ?
          (
          <Filter
            active={filter.facet_active}
            label={filter.label}
            field={filter.field}
            values={filter.values}
          />) : (
            <Sliders 
            active={filter.facet_active}
            label={filter.label}
            field={filter.field}
            range={filter.range}
            />
            )}
      </span>
        ))}        
        </span>
    </div>
)

}

export default Filters;
