import React from 'react';
import Filter from "./Filter"
import Sliders from './Sliders';

const Filters = (props) => {

  const filters = props.filters.map((filter) =>
    {
      return ({
        facet_active: filter.id,
        label: filter.sku,
        field: filter.title,
        values: filter.imageUrl,
        type: filter.type
      });    
    })


return (
    <div>
    <h2>FILTERS</h2>
    <span onChange={props.onChange}>
      <>
      {filters.type != 'slider' ?
          (
          <Filter
            active={filters.facet_active}
            label={filters.label}
            field={filters.field}
            values={filters.values}
          />) : (
            <Sliders 
            active={filters.facet_active}
            label={filters.label}
            field={filters.field}
            range={filters.range}
            />
            )}
      </>
        </span>
    </div>
)

}

export default Filters;
