import e from 'express';
import React, { useState, useEffect,useCallback } from 'react';
import ProductDisplayPage from './ProductDisplayPage';
import Searchbar from './Search/Searchbar';

const Products = () => {

    const [loading, setLoading] = useState(false);
    const [products ,setProducts] = useState([]);
    const [query, setQuery] = useState("*")

    const getQuery = (e) => {
      e.preventDefault()
      console.log(query);
    }

    const fetchProducts = useCallback(async (q) => {

      setLoading(true)

        try {

          getQuery();

            const response = await fetch(`https://scmq7n.a.searchspring.io/api/search/search.json?resultsFormat=native&siteId=scmq7n&q=${query}`);

            if(!response.ok) {
              throw new Error("Something went wrong :(")
            }

            const data = await response.json();
            console.log("DATA", data)

            const transformedProducts = data.results.map( result =>
              {
                return ({
                  id: result.id,
                  sku: result.sku,
                  title: result.title,
                  image: result.imageUrl,
                  price: result.price,
                  msrp: result.msrp,
                  url: result.url,
                });    
              }
            );

            setProducts(transformedProducts)

          } catch (error) {
            console.log(error)
        }

     setLoading(false);
      }, []);

      useEffect(() => {
        fetchProducts();
      }, [fetchProducts]);

      let content = <p>Found no products :(</p>

      if(products.length > 0) {
       content = <><Searchbar onSubmit={getQuery}/><ProductDisplayPage products={products} /></>
      }

      if(loading) {
        content = <p>Loading...</p>
      }


      return (
        <div>{content}</div>
      )
}

export default Products;
