import React from 'react';
import { NavLink } from "react-router-dom";
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';

const PDPProduct = (props) => {
  return (
    <>
    <Row>
    <Col>
      <h2>{props.title}</h2>
      <h3><strike>${parseFloat(props.msrp).toFixed(2)}</strike></h3>
      <h3>${parseFloat(props.price).toFixed(2)}</h3>
      <img src={props.image} width="500" height="700" />
      <br />
      <NavLink to={{ pathname:"/product/" +  `${props.sku}` }} className="btn btn-outline-dark">
         Buy Now
      </NavLink>
      </Col>
    </Row>
    </>
  );
};

export default PDPProduct;
