import React from 'react';
import Filter from "./Filter"
import Sliders from './Sliders';

const Filters = (props) => {

return (
    <div>
    <h2>FILTERS</h2>
    <span onChange={props.onChange}>
    {props.filters.map((filter) => (
      <>
      {filter.type != 'slider' ?
          (
          <Filter
            active={filter.facet_active}
            label={filter.label}
            field={filter.field}
            values={filter.values}
          />) : (
            <Sliders 
            active={filter.facet_active}
            label={filter.label}
            field={filter.field}
            range={filter.range}
            />
            )}
      </>
        ))}
        </span>
    </div>
)

}

export default Filters;
