  const [filter, setFilter] = useState({})
  const [value, setValue] = useState(1);

  const rangeSelector = (event) => {
      setValue(event.target.value);
    };

  const onFacetChange = () => {
    setFilter({
        active: 1,
        activeVal: props.field,
    })

