    <div>
    <h2>FILTERS</h2>
    <span onChange={onFacetChange}>
    {props.filters.map((filter) => (
      <span>
      {filter.type != 'slider' ?
          (
            <span>
            <select>
              {filter.values.map((val) => (
              <Values value={val}/>
