         <span>{props.label}</span>
            <input type="range" min="1" max={filter.range[1]} value={value}
                    class="myslider" id="sliderRange" onChange={rangeSelector}/>
            <span>Value: <span id="demo">{value}</span></span>
        </span>
            )}
      </span>
        ))}
        </span>
    </div>
)

}

export default Filters;
