//For Add Item to Cart
export const addCart = (product) => {
    return {
        type: "ADDITEM",
        payload: product
    }
    console.log(product)
}

//For Delete Item from Cart
export const delCart = (product) => {
    return {
        type: "DELETEITEM",
        payload: product
    }
}
