import React, { useState, useEffect,useCallback } from 'react';
import ProductDisplayPage from './ProductDisplayPage';
import Searchbar from './Search/Searchbar';

const Products = () => {

    const [loading, setLoading] = useState(false);
    const [products ,setProducts] = useState([]);
    const [search, setSearch] = useState("")
    const [query, setQuery] = useState("red")

    const updateSearch = (e) => {
      setSearch(e.target.value)
    }

    const getSearch = (e) => {
      e.preventDefault();
      setQuery(search)
      console.log("QUERY", query)
    }

    useEffect(() => {
      fetchProducts();
    }, [query]);

    const fetchProducts = useCallback(async () => {

      setLoading(true)

        try {

            const response = await fetch(`https://scmq7n.a.searchspring.io/api/search/search.json?resultsFormat=native&siteId=scmq7n&q=${query}`);

            if(!response.ok) {
              throw new Error("Something went wrong :(")
            }

            const data = await response.json();
            console.log("DATA", data)

            const transformedProducts = data.results.map( result =>
              {
                return ({
                  id: result.id,
                  sku: result.sku,
                  title: result.title,
                  image: result.imageUrl,
                  price: result.price,
                  msrp: result.msrp,
                  url: result.url,
                });    
              }
            );

            setProducts(transformedProducts)

          } catch (error) {
            console.log(error)
        }

     setLoading(false);
      }, []);


      return (
        <>
        {loading ? <p>Loading...</p> : ''}
        {products.length == 0 && !loading && <p>No products</p>}
        {products.length > 0 && !loading && 
        <div>
        <form onSubmit={getSearch}>
            <input type="text" value={search} onChange={updateSearch}/>
            <button type="submit">Submit</button>
         </form>
        <ProductDisplayPage products={products} />
        </div>}
        </>
      )
}

export default Products;
