import React, { useState, useEffect,useCallback } from 'react';
import ProductDisplayPage from './ProductDisplayPage';

const Products = () => {

    const [loading, setLoading] = useState(false);
    const [products ,setProducts] = useState([]);
    const [query, setQuery] = useState('');

    const handleSubmit = (event) => {
      event.preventDefault();
      setQuery('')
      console.log(query)
    };

    const Searchbar = () => {

      return (
          <div>
              <form onSubmit={handleSubmit}>
              <input type="text" value={query}/>
              <button type="submit">Submit</button>
              </form>
          </div>
        );
  }


    const fetchProducts = useCallback(async () => {
      setLoading(true)

        try {
            const response = await fetch('https://scmq7n.a.searchspring.io/api/search/search.json?resultsFormat=native&siteId=scmq7n&q=*');

            if(!response.ok) {
              throw new Error("Something went wrong :(")
            }

            const data = await response.json();
            console.log("DATA", data)

            const transformedProducts = data.results.map( result =>
              {
                return ({
                  id: result.id,
                  sku: result.sku,
                  title: result.title,
                  image: result.imageUrl,
                  price: result.price,
                  msrp: result.msrp,
                  url: result.url,
                });    
              }
            );

            setProducts(transformedProducts)

          } catch (error) {
            console.log(error)
        }

     setLoading(false);
      }, []);

      useEffect(() => {
        fetchProducts();
      }, [fetchProducts]);

      let content = <p>Found no products :(</p>

      if(products.length > 0) {
       content = <><Searchbar /><ProductDisplayPage products={products} /></>
      }

      if(loading) {
        content = <p>Loading...</p>
      }


      return (
        <div>{content}</div>
      )
}

export default Products;
