import React, { useState, useEffect,useCallback } from 'react';
import ProductDisplayPage from './ProductDisplayPage';

const Products = () => {

    const [loading, setLoading] = useState(false);
    const [products ,setProducts] = useState([]);
    const [search, setSearch] = useState("");
    const [query, setQuery] = useState(""); 


    const [currentPage, setCurrentPage] = useState(1);
    const [recordsPerPage] = useState(20);

    useEffect(() => {
      fetchProducts();
    }, [query]);

    const fetchProducts = useCallback(async () => {

      setLoading(true)

        try {

            const response = await fetch(`https://scmq7n.a.searchspring.io/api/search/search.json?resultsFormat=native&siteId=scmq7n&q=${query}`);

            if(!response.ok) {
              throw new Error("Something went wrong :(")
            }

            const data = await response.json();

            const indexOfLastRecord = currentPage * recordsPerPage;
            const indexOfFirstRecord = indexOfLastRecord - recordsPerPage;
            const nPages = Math.ceil(data.length / recordsPerPage)
            // console.log("DATA", data)

            const transformedProducts = data.results.map( result =>
              {
                return ({
                  id: result.id,
                  sku: result.sku,
                  title: result.title,
                  image: result.imageUrl,
                  price: result.price,
                  msrp: result.msrp,
                  url: result.url,
                });    
              }
            );

            setProducts(transformedProducts)

          } catch (error) {
            console.log(error)
        }

     setLoading(false);
      }, [query]);

      const updateSearch = (e) => {
        setSearch(e.target.value)
      }

      const getSearch = (e) => {
        e.preventDefault();
        setQuery(search)
        setSearch('')
      }

      return (
        <>
        <form onSubmit={getSearch}>
            <input type="text" value={search} onChange={updateSearch}/>
            <button type="submit">Submit</button>
         </form>
        {loading ? <p>Loading...</p> : ''}
        {products.length == 0 && !loading && <p>No products</p>}
        {products.length > 0 && !loading && 
        <div>
        <ProductDisplayPage products={products} />
        </div>}
        </>
      )
}

export default Products;
