import {useState} from "react";
import Values from "./Values"

const Filter = (props) => {

    return (
        <span>
            <select>
                      <option value={props.label} selected disabled>Choose a {props.label}</option>
            <span>{props.label}</span>
            {props.values.reverse().map((val) => (
                <>
            <Values value={val} defaultValue={props.label}/>
            </>
            ))}
            </select>
        </span>
    )
}

export default Filter;
