import React, { useState } from 'react';
import { NavLink } from "react-router-dom";
import { useDispatch, useSelector } from 'react-redux';
import { addCart } from '../../../redux/action';

const PLPProduct = (props) => {

  const [num, setNum] = useState(0)

  const state = useSelector((state) => state.handleCart);
  console.log(state)
  let cartNum = state.reduce(function (acc, obj) { return acc + obj.qty; }, 0);

  const dispatch = useDispatch();
  const addProduct = (props) => {
      dispatch(addCart(props));
      setNum(cartNum)
      console.log(num)
  }

  let qty_wording = ""

  if(props.quantity_available <= 10 && props.quantity_available != 1) {
    qty_wording = <p>Only {props.quantity_available} left!</p>
  } else if(props.quantity_available == 1) {
    qty_wording = <p>LAST ONE!</p>
  } else if(props.quantity_available == 0) {
    qty_wording = <p>OUT OF STOCK!</p>
  } else {
    qty_wording = <p>{props.quantity_available} available</p>
  }

  return (
    <li>
      <h2>{props.title}</h2>
      <h3><strike>${parseFloat(props.msrp).toFixed(2)}</strike></h3>
      <h3>${parseFloat(props.price).toFixed(2)}</h3>
      <h4>{qty_wording}</h4>
      <img src={props.image} width="500" height="700" />
      <h3>{props.brand}</h3>
      <p>{props.description}</p>
      <br />
      <button
            className="btn btn-outline-dark px-4 py-2"
            onClick={() => addProduct(props)}
          >
            Add to Cart
          </button>

                   <NavLink to="/cart" className="btn btn-dark ms-2 px-3 py-2">
                       Go To Cart
                   </NavLink>
    </li>
  );
};

export default PLPProduct;
