import React, {useState, useEffect, useCallback} from "react";
import { useParams } from "react-router-dom";


const Product = () => {

    const {sku} = useParams();
    console.log(sku)
    const [product, setProduct] = useState([]);
    const [loading, setLoading] = useState(false);

    const fetchProduct = useCallback(async () => {
        setLoading(true);
        const response = await fetch(`https://scmq7n.a.searchspring.io/api/search/search.json?resultsFormat=native&siteId=scmq7n&q=${sku}`)
        const data = await response.json();

        const transformedProducts = data.results.map( result =>
            {
              return ({
                id: result.id,
                sku: result.sku,
                title: result.title,
                image: result.imageUrl,
                price: result.price,
                msrp: result.msrp,
                url: result.url,
              });    
            }
          );

        setProduct(transformedProducts)
        setLoading(false);
    }, []);

    useEffect(() => {
        fetchProduct();
      }, [fetchProduct]);

    return (
        <h1>{product.name}</h1>
    )

}

export default Product;
