import React, {useState} from 'react';
import Filter from "./Filter"
import Sliders from './Sliders';

const Filters = (props) => {

  console.log("PROPS", props)

return (
    <div>
    <h2>FILTERS</h2>
    <span>
    {props.filters.map((filter) => (
      <span>
      <span>{filter.label}</span>
      {filter.type != 'slider' ?
          (
          <span onChange={props.onChange(filter.field)}>
          <Filter
            active={filter.facet_active}
            label={filter.label}
            field={filter.field}
            values={filter.values}
          />
          </span>) : (
            <span onChange={props.onChange(filter.range)}>
            <Sliders 
            active={filter.facet_active}
            label={filter.label}
            field={filter.field}
            range={filter.range}
            />
            </span>
            )}
      </span>
        ))}        
        </span>
    </div>
)

}

export default Filters;
