import React, { useState, useEffect, useCallback } from 'react';
import ProductDisplayPage from './ProductDisplayPage';
import Filters from '../../Filters/Filters';

const Products = () => {

    const [loading, setLoading] = useState(false);
    const [products ,setProducts] = useState([]);
    const [search, setSearch] = useState("");
    const [query, setQuery] = useState("");
    const [currentPage, setCurrentPage] = useState(1)
    const [filters, setFilters] = useState({});
    const [activeFilterValues, setActiveFilterValues] = useState({});
    const [activeFilters, setActiveFilters] = useState([])
    const [pageData, setPageData] = useState({});

    function isObjEmpty (obj) {
      return Object.keys(obj).length === 0;
  }

  let isEmpty = isObjEmpty(activeFilterValues)

    useEffect(() => {
      fetchProducts(currentPage);
    }, [query, currentPage, activeFilterValues]);

    useEffect(() => {
      if(!isEmpty) {
        setActiveFilters(filters => [...filters, activeFilterValues])
       }
    }, [activeFilterValues])

    const fetchProducts = useCallback(async () => {

      setLoading(true)

        try {

          let selectedFilters;
          let response;

          console.log("activeFilterValues", activeFilterValues)
          console.log("activeFilters", activeFilters)

           if(activeFilters.length > 1) {
            console.log("FIRST IF ----")
            activeFilters.map(filter => console.log(filter))
          } else if(!isEmpty) {
            console.log("SECOND IF ----")
            selectedFilters = "&filter." + activeFilterValues.activeFac + "=" + activeFilterValues.activeVal;
          }

          if(selectedFilters) {
            response = await fetch(`https://scmq7n.a.searchspring.io/api/search/search.json?resultsFormat=native&siteId=scmq7n&q=${query}&page=${currentPage}` + selectedFilters);
          } else {
            response = await fetch(`https://scmq7n.a.searchspring.io/api/search/search.json?resultsFormat=native&siteId=scmq7n&q=${query}&page=${currentPage}`);
          }

            if(!response.ok) {
              throw new Error("Something went wrong :(")
            }

            const data = await response.json();

            const transformedProducts = data.results.map(result =>
              {
                return ({
                  id: result.id,
                  sku: result.sku,
                  title: result.title,
                  image: result.imageUrl,
                  price: result.price,
                  msrp: result.msrp,
                  url: result.url,
                });    
              }
            );

            const transformedFilters = data.facets.map(filter =>
              {
                return ({
                  field: filter.field,
                  label: filter.label,
                  type: filter.type,
                  facet_active: filter.facet_active,
                  range: filter.range,
                  values: filter.values,
                });    
              }
            );

            setPageData(data.pagination);
            setProducts(transformedProducts)
            setFilters(transformedFilters)

            console.log(transformedFilters)

          } catch (error) {
            console.log(error)
        }

     setLoading(false);
      }, [query, currentPage, activeFilterValues]);

      const updateSearch = (e) => {
        setSearch(e.target.value)
      }

      const getSearch = (e) => {
        e.preventDefault();
        setQuery(search)
        setSearch('')
      }


      const prevPageHandler = () => {
        setCurrentPage(pageData.currentPage - 1)
      }

      const nextPageHandler = () => {
        setCurrentPage(pageData.currentPage + 1)
      }

      const onOptionChangeHandler = (fac, label) => (event) => {
          setActiveFilterValues({
            active: true,
            activeFac: fac,
            activeLabel: label,
            activeVal: event.target.value,
        })
    }

  //   useEffect(() => {

      // setActiveFilters(filters => [...filters, activeFilterValues])


  // },[activeFilterValues]) // <-- here put the parameter to listen

  // console.log("AFV", activeFilterValues)
  // console.log("ACTIVE FILTERS", activeFilters)

      return (
        <>
        <form onSubmit={getSearch}>
            <input type="text" value={search} onChange={updateSearch}/>
            <button type="submit">Submit</button>
         </form>
        {loading ? <p>Loading...</p> : ''}
        {products.length == 0 && !loading && <p>No products</p>}
        {products.length > 0 && !loading && 
        <div>
        <Filters filters={filters} onChange={onOptionChangeHandler}/>
        <div>
          {activeFilterValues.activeLabel && 
          <>
          <h3>Enabled Filters:</h3>
         <div>{activeFilterValues.activeLabel} = {activeFilterValues.activeVal}</div>
         </>}
        </div>
        <p>{pageData.totalResults} results</p>
        <ProductDisplayPage products={products} />
        <p>Page {pageData.currentPage} of {pageData.totalPages}</p>
        {pageData.previousPage !== 0 && <button value={pageData.previousPage} onClick={prevPageHandler}>←</button>}
        {pageData.nextPage !== 0 && <button value={pageData.nextPage} onClick={nextPageHandler}>→</button>}
        </div>}
        </>
      )
}

export default Products;
