            if(!response.ok) {
              throw new Error("Something went wrong :(")
            }

            const data = await response.json();
            console.log("DATA", data)

          } catch (error) {
            console.log(error)
        }

     setLoading(false);
      }, []);

      useEffect(() => {
        fetchProducts();
      }, [fetchProducts]);

